import { Component, OnInit } from '@angular/core';
import { UntypedFormGroup, UntypedFormBuilder } from '@angular/forms';
import { ApiService } from '../shared/api.service';
import { RestaurentData } from './restaurent.model';

@Component({
  selector: 'app-restaurent-dash',
  templateUrl: './restaurent-dash.component.html',
  styleUrls: ['./restaurent-dash.component.css'],
})
export class RestaurentDashComponent implements OnInit {
  formValue!: UntypedFormGroup;
  restaurentModelObj: RestaurentData = new RestaurentData();
  allRestaurentData: any;
  showAdd!: boolean;
  showBtn!: boolean;

  constructor(
    private formbuilder: UntypedFormBuilder,
    private api: ApiService
  ) {}

  ngOnInit(): void {
    this.formValue = this.formbuilder.group({
      name: [''],
      email: [''],
      mobile: [''],
      address: [''],
      services: [''],
    });
    this.getAllData();
  }
  clickAddResto() {
    this.formValue.reset();
    this.showAdd = true;
    this.showBtn = false;
  }

  addRestaurent() {
    this.restaurentModelObj.name = this.formValue.value.name;
    this.restaurentModelObj.email = this.formValue.value.email;
    this.restaurentModelObj.mobile = this.formValue.value.mobile;
    this.restaurentModelObj.address = this.formValue.value.address;
    this.restaurentModelObj.services = this.formValue.value.services;

    this.api.postRestaurent(this.restaurentModelObj).subscribe(
      (res) => {
        console.log(res);
        alert('Restaurent Added Successfully');
        this.formValue.reset();

        let ref = document.getElementById('close');
        ref?.click();

        this.getAllData();
      },
      (err) => {
        console.log(err);
        alert('Restaurent Added Failed!');
      }
    );
  }

  getAllData() {
    this.api.getRestaurent().subscribe(
      (res) => {
        this.allRestaurentData = res;
      },
      (err) => {
        console.log(err);
      }
    );
  }

  deleteResto(data: any) {
    this.api.deleteRestaurant(data).subscribe((res: any) => {
      console.log(res);
      alert('Restaurent Deleted Successfully');
      this.getAllData();
    });
  }

  onEditResto(data: any) {
    this.showAdd = false;
    this.showBtn = true;

    this.restaurentModelObj.id = data.id;
    this.formValue.controls['name'].setValue(data.name);
    this.formValue.controls['email'].setValue(data.email);
    this.formValue.controls['mobile'].setValue(data.mobile);
    this.formValue.controls['address'].setValue(data.address);
    this.formValue.controls['services'].setValue(data.services);
  }
  updateResto() {
    this.restaurentModelObj.name = this.formValue.value.name;
    this.restaurentModelObj.email = this.formValue.value.email;
    this.restaurentModelObj.mobile = this.formValue.value.mobile;
    this.restaurentModelObj.address = this.formValue.value.address;
    this.restaurentModelObj.services = this.formValue.value.services;

    this.api
      .updateRestaurant(this.restaurentModelObj.id, this.restaurentModelObj)
      .subscribe((res: any) => {
        alert('Restaurent Updated Successfully');
        this.formValue.reset();

        let ref = document.getElementById('close');
        ref?.click();

        this.getAllData();
      });
  }
}
<body>
  <nav class="navbar navbar-light bg-primary">
    <div class="container-fluid">
      <h2>Restaurent Record App</h2>
      <div class="d-flex">
        <button
          (click)="clickAddResto()"
          class="btn btn-success"
          type="button"
          data-bs-toggle="modal"
          data-bs-target="#exampleModal"
        >
          Add Restaurent
        </button>

        <button
          routerLink="/login"
          style="margin-left: 5px"
          class="btn btn-danger"
          type="button"
          data-bs-toggle="modal"
        >
          Log Out
        </button>
      </div>
    </div>
  </nav>
  <div class="p-5 mb-4 bg-light rounded-3">
    <div class="container-fluid py-5">
      <h1 class="display-5 fw-bold">Restaurent Records Management System</h1>
      <p class="col-md-8 fs-4">You can save Records of Restaurent</p>
      <!-- <a class="btn btn-primary btn-lg" type="button" target="_blank" href="https://github.com/sibashish99/Restaurent_Application">Source Code</a> -->
    </div>
  </div>
  <table class="table mt-4">
    <thead>
      <tr>
        <th scope="col">Restaurent Id</th>
        <th scope="col">Restaurent Name</th>
        <th scope="col">Restaurent Email</th>
        <th scope="col">Restaurent Address</th>
        <th scope="col">Restaurent Phone</th>
        <th scope="col">Restaurent Services</th>
        <th scope="col">Action</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let data of allRestaurentData">
        <td>{{ data.id }}</td>
        <td>{{ data.name }}</td>
        <td>{{ data.email }}</td>
        <td>{{ data.address }}</td>
        <td>{{ data.mobile }}</td>
        <td>{{ data.services }}</td>
        <td>
          <button
            (click)="onEditResto(data)"
            class="btn btn-info"
            type="button"
            data-bs-toggle="modal"
            data-bs-target="#exampleModal"
          >
            Edit
          </button>
          &nbsp;
          <button (click)="deleteResto(data.id)" class="btn btn-danger">
            Delete
          </button>
        </td>
      </tr>
    </tbody>
  </table>

  <!-- Modal -->
  <div
    class="modal fade"
    id="exampleModal"
    tabindex="-1"
    aria-labelledby="exampleModalLabel"
    aria-hidden="true"
  >
    <div class="modal-dialog">
      <div class="modal-content">
        <div class="modal-header">
          <h5 class="modal-title" id="exampleModalLabel">Records</h5>
          <button
            type="button"
            class="btn-close"
            data-bs-dismiss="modal"
            aria-label="Close"
          ></button>
        </div>
        <div class="modal-body">
          <form [formGroup]="formValue">
            <div class="mb-3">
              <label class="form-label">Name</label>
              <input
                type="text"
                class="form-control"
                formControlName="name"
                placeholder="Restaurent Name"
                id="exampleInputEmail1"
                aria-describedby="emailHelp"
              />
            </div>
            <div class="mb-3">
              <label class="form-label">Email</label>
              <input
                type="email"
                class="form-control"
                formControlName="email"
                placeholder="abcd@gmail.com"
                id="exampleInputPassword1"
              />
            </div>
            <div class="mb-3">
              <label class="form-label">Address</label>
              <input
                type="text"
                class="form-control"
                formControlName="address"
                placeholder="Abc st, city, Postal code: M9E 8H0"
                id="exampleInputPassword1"
              />
            </div>
            <div class="mb-3">
              <label class="form-label">Mobile</label>
              <input
                type="number"
                class="form-control"
                formControlName="mobile"
                placeholder="+1-123-456-7890"
                id="exampleInputPassword1"
              />
            </div>
            <div class="mb-3">
              <label class="form-label">Services</label>
              <input
                type="text"
                class="form-control"
                formControlName="services"
                placeholder="Tandoori momos, Butter chicken"
                id="exampleInputPassword1"
              />
            </div>
          </form>
        </div>
        <div class="modal-footer">
          <button
            type="button"
            class="btn btn-secondary"
            data-bs-dismiss="modal"
            id="clear"
          >
            Close
          </button>
          <button
            *ngIf="showAdd"
            type="button"
            class="btn btn-primary"
            (click)="addRestaurent()"
          >
            Add Details
          </button>
          <button
            *ngIf="showBtn"
            type="button"
            class="btn btn-primary"
            (click)="updateResto()"
          >
            Update
          </button>
        </div>
      </div>
    </div>
  </div>
</body>
